        let orderReversed = true;
        changeOrder(0);
        
        function searchClick() {
            let value = searchBar.value;
            if (!value) return;
            window.location.assign(""/products?q="" + value);
        }
        
        function clearClick() {
            window.location.assign(""/products"");
        }
        
